import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Navbar.css'; // Ensure this path matches your directory structure

function Navbar() {
  // State to track if the user is logged in and their username
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  // Dummy function to simulate login (you will replace this with your actual login logic)
  const handleLogin = () => {
    setIsLoggedIn(true); // Update state to reflect logged in state
    setUsername('testuser'); // Replace 'testuser' with actual username from login response
    // Perform actual login actions (e.g., store token, fetch user data, etc.)
  };

  // Dummy function to simulate logout (you will replace this with your actual logout logic)
  const handleLogout = () => {
    setIsLoggedIn(false); // Update state to reflect logged out state
    setUsername(''); // Clear username
    // Perform actual logout actions (e.g., clear token, redirect, etc.)
  };

  return (
    <nav>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/contact">Contact</Link></li>
        <li><Link to="/books">Books</Link></li>
        {isLoggedIn ? (
          <>
            <li>Welcome, {username}</li>
            <li><Link to="/profile">Profile</Link></li>
            <li><button onClick={handleLogout}>Logout</button></li>
          </>
        ) : (
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/signup">Signup</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
