import React, { useState } from 'react';
import QuillEditor from './QuillEditor';
import useAuthToken from '../hooks/useAuthToken'; // Adjust the path based on your project structure

const AddBook = () => {
  const { token } = useAuthToken(); // Retrieve the authentication token
  const [content, setContent] = useState('');
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');

  const handleSubmit = async () => {
    const bookData = {
      title,
      author,
      content,
    };

    console.log('Submitting book data:', bookData);

    try {
      const response = await fetch('http://localhost:5000/api/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}` // Ensure token is included in headers
        },
        body: JSON.stringify(bookData),
      });

      console.log('Response status:', response.status);

      if (response.ok) {
        const result = await response.json();
        console.log('Book added successfully:', result);
      } else {
        const errorData = await response.json();
        console.error('Failed to add book:', errorData);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Add a New Book</h1>
      <input 
        type="text" 
        placeholder="Title" 
        value={title} 
        onChange={(e) => setTitle(e.target.value)} 
      />
      <input 
        type="text" 
        placeholder="Author" 
        value={author} 
        onChange={(e) => setAuthor(e.target.value)} 
      />
      <QuillEditor content={content} setContent={setContent} />
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default AddBook;
