import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Cookies from 'js-cookie';
import booksphereLogo from '../../public/booksphere-logo.jpg'; // Import your logo
import '../styles/Home.css';

function Home() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      const token = Cookies.get('token');
      if (!token) {
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/users/me', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setUser(res.data);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    fetchUser();
  }, []);

  return (
    <div className="home-container">
      <header className="header">
        <div className="header-content">
          <img src={booksphereLogo} alt="BookSphere Logo" className="logo" /> {/* Display your logo */}
          <h1>Welcome to BookSphere</h1>
          <p>Your ultimate destination for exploring and discovering new books!</p>
          {user ? (
            <div className="user-info">
              <p>Welcome, {user.name}!</p>
              <p>Email: {user.email}</p>
            </div>
          ) : (
            <Link to="/login" className="btn">
              Get Started
            </Link>
          )}
        </div>
      </header>

      {/* Rest of your component remains the same */}
      {/* ... */}
    </div>
  );
}

export default Home;
